import React from 'react';
import axios from 'axios';

const KEY = 'AIzaSyBdXjGbMZ7Yd_W3digAhPLAjnKWACgL5Us';

class Search extends React.Component {
    constructor() {
        super()
        this.state = {
            search:'',
            region: 'IN',
            tittle:''
        }
    }

    searchFeild = (event) => {
        this.setState({
            search: event.target.value
        })
    }

    // handleChange = (e) => {
    //     this.setState({ [e.target.name]: e.target.value });
    // }

    youtubeApi = async () => {
        console.log(this.state.search);
        const serachApi = await axios.get(`https://www.googleapis.com/youtube/v3/search?key=${KEY} `)
            .then(res => {
                const data = res.data;
                console.log(data);
                this.setState({
                    tittle:data.regionCode
                })
            })

            .catch(err => console.log(err))
    }

    render() { 
        return (
            <div>
                <input type='text' placeholder='Search here' value={this.state.search} onChange={this.searchFeild} />
                <button onClick={() => this.youtubeApi()} > Search </button>   <br/>
                {this.state.tittle}
            </div>
        )
    }

}

export default Search;

