import React from 'react';
import axios from 'axios';

const KEY = 'AIzaSyBdXjGbMZ7Yd_W3digAhPLAjnKWACgL5Us';

class Search extends React.Component {
    constructor() {
        super()
        this.state = {
        }
    }

    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    youtubeApi = async () => {
        const api = await axios.get(`https://www.googleapis.com/youtube/v3/videos?id=7lCDEYXw3mM&key=${KEY}&part=snippet,contentDetails,statistics,status`)
            .then(res => {
                const data = res.data;
                console.log(data);
                var id = data.items[0].snippet.resourceId;
                console.log(id);
                document.getElementById('youtube_feed').innerHTML = `<iframe width="1280" height="720" src="https://www.youtube.com/embed/${id}" 
                frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>`
            })
            .catch(err => console.log(err))
    }

    render() {
        return (
            <div>
                <input type='text' name='search' placeholder='Search here' value={this.state.Search} onChange={this.handleChange} />
                <button onClick={() => this.youtubeApi()} > Search </button>
            </div>
        )
    }

}

export default Search;

