import React from 'react';
import axios from 'axios';

const KEY = 'AIzaSyBdXjGbMZ7Yd_W3digAhPLAjnKWACgL5Us';

class Search extends React.Component {
    constructor() {
        super()
        this.state = {
            search:'',
        }
    }

    search = (event) => {
        this.setState({
            search: event.target.value
        })
    }

    // handleChange = (e) => {
    //     this.setState({ [e.target.name]: e.target.value });
    // }

    youtubeApi = async () => {
        console.log(this.state.Search);

        const api = await axios.get(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=${this.state.search}&key=${KEY} HTTP/1.1`)
            .then(res => {
                const data = res.data;
                console.log(data);
            })
            .catch(err => console.log(err))
    }

    render() {
        return (
            <div>
                <input type='text' name='search' placeholder='Search here' value={this.state.search} onChange={this.search} />
                <button onClick={() => this.youtubeApi()} > Search </button>
            </div>
        )
    }

}

export default Search;



